import Header from '../layout/Header';
import MainPage from '../layout/MainPage';
import Footer from '../layout/Footer';
import { BrowserRouter } from 'react-router-dom';

class Main extends Component{
  constructor(props){
    super(props);
    this.state = {
      pathName: null,
      path: '/',
    };
  }

  getPathName = (path) => {
    if(path === '프로필') {
      this.setState({
        path : 'Profile',
      })
    }
    console.log('reload...');
    // window.location.reload();
    this.RenderMainView(this.state.path);
  }

  RenderMainView = (path) => {
    console.log(path);
    return <MainPage routerName={path}/>;
  }

  render(){
    // const renderMainPage = (data) => {
    //   return <MainPage routerName={data}/>;
    // };

    return (
        <>
          <Header getRouter={this.getPathName} />
          <BrowserRouter>
            <MainPage />
            {/* {this.RenderMainView(this.state.path)} */}
          </BrowserRouter>
          <Footer />
        </>
    )
  }
}

export default Main;
