import React , { Component } from 'react';
import code from '../img/code.jpeg';

const styles = theme => ({
    root: {
        width: "100%",
        minWidth: 1080,
        backgroundImage: "url(../img/code.jpeg)",
    }
});

class Board extends Component{
    state = {
        text : '',
    }
    handleChange = (e) => {
        this.setState({
            // text : e.target.value
            [e.target.name] : e.target.value
        })
    }



    render(){
        return(
            <div >
                {code}
            </div>
        )
    }
}



export default Board;
