import * as types from '../actions/ActionTypes';

const initialState = {
    login: {
        status: 'INIT'
    },
    status: {
        valid: false,
        isLoggedIn: false,
        currentUser: '',
    }
};

export default function authentication(state = initialState, action){
    switch(action.type){
        case types.AUTH_LOGIN:
            return{
                ...state,
                login : {
                    status: 'WAITING'
                }
            }
        case types.AUTH_LOGIN_SUCCESS:
            return{
                // ...state,
                login : {
                    status: 'SUCCESS'
                },
                status: {
                    // ...state.status,
                    isLoggedIn: true,
                    currentUser: action.username
                  }
            }
        case types.AUTH_LOGIN_FAIL:
            return{
                // ...state,
                login : {
                    status: 'FAIL'
                }
            }
        default:
            return state;
    }
};
