              .catch(err => console.log(err));
        }

        handleValueChange = (e) => {
            let nextState = {};
            nextState[e.target.name] = e.target.value;
            this.setState(nextState);
          }

          handeSlideMenu = (e) => {
            console.log('메뉴리스트');
