import {
    AUTH_LOGIN,
    AUTH_LOGIN_SUCCESS,
    AUTH_LOGIN_FAIL
} from './ActionTypes';
import axios from 'axios';

const config = {
    headers: {
        'Content-Type': 'application/x-www-form-urlencoded;charset=UTF-8'
    }
}

export function loginRequest(data) {
    return(dispatch) => {
        // Login API Start
        dispatch(login());

        // API REQUEST
        return axios.post('/dr/member/login' , data , config)
        .then((response) => {
            console.log(data.userEmail);
            console.log(data.get('userEmail'));
            dispatch(loginSuccess(data.userEmail));
        }).catch((error) => {
            dispatch(loginFail());
        });
    }
}

export function login(){
    return {
        type: AUTH_LOGIN
    };
}

export function loginSuccess(userEmail){
    return {
        type: AUTH_LOGIN_SUCCESS,
        userEmail
    };
}

export function loginFail(){
    return {
        type: AUTH_LOGIN_FAIL
    };
}
