import React , {Component} from 'react';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import Divider from '@material-ui/core/Divider';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import {withStyles} from '@material-ui/core/styles';

const styles = theme => ({
    root: {
        '& > *': {
          margin: theme.spacing(1),
          width: '25ch',
        },
      },
});

class BoardWrite extends Component{
    constructor(props){
        super(props);
        this.state = {
            title: '',
        }
    }

    handleValueChange = (e) => {
        let nextState = {};
        nextState[e.target.name] = e.target.value;
        this.setState(nextState);
    }

    render(){
        const { classes } = this.props;
        return (
            <div classes={classes}>
                <CssBaseline />
                <Container maxWidth="md">
                <Typography component="div" style={{ backgroundColor: '#F6F6F6' , minHeight:'100%' , fontSize: '15px' , textAlign: 'left'}}>
                    <div>
                        제목 : <TextField autoFocus margin="dense" id="title" name="title" value={this.state.title} label="text" type="text" onChange={this.handleValueChange} fullWidth /> <br/>
                        작성자 :  <br/>
                        <br /><br />
                        <Divider />
                        내용 :  <br/> 
                        <br /><br />
                    </div>
                    <Divider />
                </Typography>
                </Container>
            </div>
        )
    }
}

export default withStyles(styles)(BoardWrite);
