verifyEmail = (email) => {
    let regExp = /^[0-9a-zA-Z]([-_.]?[0-9a-zA-Z])*@[0-9a-zA-Z]([-_.]?[0-9a-zA-Z])*.[a-zA-Z]{2,3}$/i;
    if(email.match(regExp) == null || email === null || email === ''){
        alert('Please check your email'); 
        return 'FAIL';
    }else{
        this.setState({
            userEmailStatus: 1
        });
        return 'SUCCESS';
    }
}

export default verifyEmail;
