import React , { Component } from 'react';

class Board extends Component{
    state = {
        text : '',
    }
    handleChange = (e) => {
        this.setState({
            // text : e.target.value
            [e.target.name] : e.target.value
        })
    }
    render(){
        return(
            <div>
                Board <br></br>
                <input type="text" name="text" value={this.state.text} onChange={this.handleChange} />
                {this.state.text}
            </div>
        )
    }
}



export default Board;
