import React , {Component} from 'react';
import { withStyles } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import MenuIcon from '@material-ui/icons/Menu';
import IconButton from '@material-ui/core/IconButton';
import axios from 'axios';
// import {API} from '../api/Call_API';


const styles = theme => ({
    list: {
      width: 250,
    },
    fullList: {
      width: 'auto',
    },
  });

class Menu extends Component{
    constructor(props){
        super(props);
        this.state = {
            left: false,
            menuItems: '',
        }
    }

    componentWillMount(){
        // API.GET_Categories()
          this._GETCategories()
          .then(res => { 
            console.log(res);
            this.setState({
              // menuItems : res.data.data,
            })
          })
          .catch(err => {console.log(err); });
    }

    _GETCategories = async() => {
      const response = await axios.get('/dr/category/list');
      const body = await response.data.data.json();
      return body;
    }

    toggleDrawer = (side, open) => event => {
        if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
          return;
        }

        this.setState({ ...this.state, [side]: open });
      };

    handleClickMenu = (menu) => {
        console.log(menu);
        this.props.getRouter(menu);
      }

    divStyle = {
        list: {
            width: 250,
          },
    }

    openSide = side => (
        <div
          role="presentation"
          onClick={this.toggleDrawer(side, false)}
          onKeyDown={this.toggleDrawer(side, false)}
          style={this.divStyle.list}
        >
          {this.menuList(this.state.menuItems)}
        </div>
      );

      // {['Inbox2222', 'Starred222' , 'Send email', 'Drafts'].map((menu) => (
      menuList = () => (
        <List>
            {/* {['Inbox2222', 'Starred222' , 'Send email', 'Drafts'].map((menu) => (
            <ListItem 
              button key={menu}
              onClick={() => {this.handleClickMenu(menu)}}
            >
              <ListItemText primary={menu}/>
             </ListItem>
          ))} */}
          {this.renderMenu()}
        </List>
    );

    renderMenu = () => {
        const menuItems = this.state.menuItems;
        console.log(menuItems);
        // return menuItems.map((c) => {
        //   return <ListItem 
        //             button key={c.name}
        //             onClick={() => {this.handleClickMenu(c.name)}}
        //           >
        //             <ListItemText primary={c.name}/>
        //           </ListItem>
        // })
    }

    render(){
        const {classes} = this.props;
        return(
            <div>
                <IconButton 
                    className={classes.menuButton} 
                    color='inherit' 
                    aria-label="Open drawer"
                    onClick={this.toggleDrawer('left', true)}
                >
                  {this.state.menuItems}
                    <MenuIcon />
                </IconButton>
                <Drawer open={this.state.left} onClose={this.toggleDrawer('left', false)}>
                    {this.openSide('left')}
                </Drawer>
            </div>
        )
    }
}

export default withStyles(styles)(Menu);
